import './App.css';
import React, {  useState,useEffect } from "react";
import Axios from 'axios';
import { useParams,} from "react-router-dom";





function App() {

let {postId} = useParams();
const [post,setPost] = useState({});

const [resumeid,id] = useState('');
const [resumename,name1] = useState('');
const [resumeus,usn] = useState('');

var pen = document.getElementById(id.value);

useEffect(()=>{
  Axios.get(`http://localhost:3001/api/${postId}`).then((data)=>{
      console.log(data)
  setPost({

           usn: data.data[0].usn,
           name: data.data[3].name,
           id:data.data[3].id
          });
   });

  },[postId]);

const submitReview=()=>{
  Axios.post("http://localhost:3001/", {
    resumeid: resumeid, 
    resumename: resumename,
    resumeus: resumeus,
}).then(()=> {
  alert("sucessfully inserted")

});
};





  return (
    <div id='main'>

      <div><h1>RESUME BUILDER</h1></div>
      <div>
        <form><tr><td>
        <label>ID</label></td><td>
        <input type="text" name="id" id ="tf" onChange={(e)=>{
          id(e.target.value)
        }}/></td></tr>
        <input type="text" name="usn" onChange={(e)=>{
          usn(e.target.value)
        }}/>

        <input type="text" name="name1" onChange={(e)=>{
          name1(e.target.value)
        }}/>

        <button onClick={submitReview}>SUBMIT

        </button></form><br/>

        {post.usn}<br/> {post.id}<br/> {post.name}<br/>
      </div>




    </div>


  );
}

export default App;
