import './App.css';
import React, {  useState,useEffect } from "react";
import Axios from 'axios';
import {Form, useParams,Route,Link, Router} from "react-router-dom";
import './maini';




function App() {

let {postId} = useParams();
const [post,setPost] = useState({});

const [resumeid,id] = useState('');
const [resumename,name1] = useState('');
const [resumeus,usn] = useState('');


useEffect(()=>{
  Axios.get(`http://localhost:3001/api/${postId}`).then((data)=>{
      console.log(data)
  setPost({

           usn: data.data[0].usn,
           name: data.data[0].name,
           id:data.data[0].id
          });
   });

  },[postId]);

const submitReview=()=>{
  Axios.post("http://localhost:3001/", {
    resumeid: resumeid, 
    resumename: resumename,
    resumeus: resumeus,
}).then(()=> {
  alert("sucessfully inserted")

});
};





  return (
    <div>
      <div className='App'>
        <Router exact to="/display" component={maini} />

      </div>
      <div><h1>RESUME BUILDER</h1></div>
      <div>

        <label>id</label>
        <input type="text" name="id" onChange={(e)=>{
          id(e.target.value)
        }}/>
        <input type="text" name="usn" onChange={(e)=>{
          usn(e.target.value)
        }}/>

        <input type="text" name="name1" onChange={(e)=>{
          name1(e.target.value)
        }}/>

        <button onClick={submitReview}>SUBMIT

        </button>
        {post.usn}
      </div>
      <div className="Post individual">
        <h1 className="post-title"></h1>
          <p>{post.usn}</p>
          <h4>{post.name}</h4>


      </div>



    </div>


  );
}

export default App;
