import './App.css';
import React, {  useState,useEffect } from "react";
import Axios from 'axios';
import { useParams,} from "react-router-dom";





function App() {

let {postId} = useParams();
const [post,setPost] = useState({});

const [resumeid,id] = useState('');
const [resumename,name1] = useState('');
const [resumeus,usn] = useState('');
const [resumehdeg,hdeg] = useState('');
const [resumejtype,jtype] = useState('');
const [resumejdic,jdisc] = useState('');
const [resumesslcp,sslcp] = useState('');
const [resumesslcins,sslcins] = useState('');
const [resumepucp,pucp] = useState('');
const [resumepuci,puci] = useState('');
const [resumedeg,deg] = useState('');
const [resumedegp,degp] = useState('');
const [resumedegi,degi] = useState('');




var pen = document.getElementById(id.value);

useEffect(()=>{
  Axios.get(`http://localhost:3001/api/${postId}`).then((data)=>{
      console.log(data)
  setPost({
           hdeg: data.data[10].hdeg,
           usn: data.data[0].usn,
           name: data.data[3].name,
           id:data.data[3].id
          });
   });

  },[postId]);

const submitReview=()=>{
  Axios.post("http://localhost:3001/", {
    resumeid: resumeid, 
    resumename: resumename,
    resumeus: resumeus,
    resumehdeg: resumehdeg,

}).then(()=> {
  alert("sucessfully inserted")

});
};

  return (
    <div class="context">
    <div  >
      <div >

            <h1>RESUME BUILDER</h1>
            </div>
      <div class="area">

         <ul class="circles">
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
