import Axios from 'axios';
import React, {  useState,useEffect } from "react";
import {Form, useParams,Route,Link} from "react-router-dom"




function App() {
let {postId} = useParams();
const [post,setPost] = useState({});






useEffect(()=>{
    Axios.get(`http://localhost:3001/api/${postId}`).then((data)=>{
        console.log(data)
    setPost({
            
             usn: data.data[0].usn,
             name: data.data[0].name,
             id:data.data[0].id
            });
     });
    
    },[postId]);
    return(

      <div className="Post individual">
        <h1 className="post-title"></h1>
          <p>{post.usn}</p>
          <h4>{post.name}</h4>
          
          
      </div>
      );
}

export default maini;
